/*
 *  leetcode 6:
 *  Level    : easy
 *  You're given strings J representing the types of stones that are jewels, and S representing the stones you have.  Each character in S is a type of stone you have.  You want to know how many of the stones you have are also jewels.
 *
 *  The letters in J are guaranteed distinct, and all characters in J and S are letters. Letters are case sensitive, so "a" is considered a different type of stone from "A".
 *
 *  Example 1:
 *
 *  Input: J = "aA", S = "aAAbbbb"
 *  Output: 3
 *  Example 2:
 *
 *  Input: J = "z", S = "ZZ"
 *  Output: 0
 */
int numJewelsInStones(char* J, char* S) {
    char*               pcJew               = NULL;
    int                 i                   = 0;
    int                 j                   = 0;
    int                 k,m                 = 0;
    int                 iSum                = 0;
    int                 iExist              = 0;

    pcJew = (char*)malloc (strlen(J));
    memset(pcJew, 0, strlen(J));
    assert(pcJew != NULL);

    /* get diff type in J */    
    while (i < strlen(J))
    {
        iExist = 0;
        if (!strlen(pcJew))
        {
            pcJew[j++] = J[i++];
            continue;
        }
        else
        {
            while (k < strlen(pcJew))
            {
                if (pcJew[k] == J[i])
                {
                    iExist  = 1; 
                    break;
                }
                else
                {
                    k++;
                }
            }
            
            if (iExist)
            {
                i++;
                continue;
            }
            else
            {
                pcJew[j++] = J[i++];
            }
        }
    }
    
    /* serach stones */
    i = 0;
    while (i < j)
    {
        m = 0;
        while (m < strlen(S)) 
        {
            if (pcJew[i] == S[m])
            {
                iSum++;
            }
            m++;
        }
printf("iSum = [%d].\n", iSum);
        i++;
    }
    
    free(pcJew);
    return iSum;
}

/*
 *  leetcode 5:
 *  Level    : easy
    Evaluate the value of an arithmetic expression in Reverse Polish Notation.

    Valid operators are +, -, *, /. Each operand may be an integer or another expression.

    Note:

    Division between two integers should truncate toward zero.
    The given RPN expression is always valid. That means the expression would always evaluate to a result and there won't be any divide by zero operation.
    Example 1:

    Input: ["2", "1", "+", "3", "*"]
    Output: 9
    Explanation: ((2 + 1) * 3) = 9
 */

int evalRPN(char** tokens, int tokensSize) {
    int                 iTop                = 0;
    int                 i                   = 0;
    int*                pi                  = NULL;

    int                 m,n                 = 0;
    int                 r                   = 0;
    char                *endptr             = NULL;

    pi = malloc(sizeof (int) * tokensSize);

    while (i < tokensSize)
    {
        if (!strcmp(tokens[i], "+")
            ||(!strcmp(tokens[i], "-"))
            ||(!strcmp(tokens[i], "*"))
            ||(!strcmp(tokens[i], "/")))
        {
            m = *(pi + iTop - 1);
            n = *(pi + iTop - 2);
            switch (*(tokens[i]))
            {
                case '+':
                {
                    r = n + m; 
                    break;
                }
                case '-':
                {
                    r = n - m; 
                    break;
                }
                case '*':
                {
                    r = n * m; 
                    break;
                }
                case '/':
                {
                    r = n / m; 
                    break;
                }
                default:
                {
                    break;
                }
            }
            iTop = iTop - 2; 
            /* push */
            pi[iTop++] = r;

            printf("push [%d].\n", pi[iTop -1]);
        }
        else
        {
            pi[iTop++] = strtol(tokens[i], &endptr, 10);
            printf("push [%d].\n", pi[iTop -1]);
        }
        i = i + 1; 
    }

    r = *pi; 
    free(pi);

    return r;
}

/*
 * leetcode 4:
 * Level    : easy
 * Implement function ToLowerCase() that has a string parameter str, and returns the same string in lowercase.
 *
 */
char* toLowerCase(char* str) {
    int             i               = 0;
    assert(str != NULL);
    
    while (i < strlen(str))
    {
        if ((*(str + i) > 64) 
            && (*(str + i) < 91))
        {
           *(str + i) = *(str + i) + 32; 
        }

        i++;
    }

    return str;
}

